"use strict";(self.webpackChunkwrapkit=self.webpackChunkwrapkit||[]).push([[110],{110:t=>{t.exports=JSON.parse('[{"imgSrc":"../images/novel/1.jpg","title":"\ub370\ubbf8\uc548","author":"\ud5e4\ub974\ub9cc \ud5e4\uc138","publicationDate":"1919","description":"\ubd88\uc548\ud55c \uc80a\uc74c\uc5d0 \ubc14\uce58\ub294 \ud5e4\ub974\ub9cc \ud5e4\uc138\uc758 \uc601\ud63c\uc758 \uc774\uc57c\uae30!","description2":"\ud604\uc2e4\uc5d0 \ub300\uacb0\ud558\ub294 \uc601\ud63c\uc758 \ubc1c\uc804\uc744 \ub2f4\uc740 \ud5e4\ub974\ub9cc \ud5e4\uc138\uc758 \uac78\uc791 \u300e\ub370\ubbf8\uc548\u300f. \ub3c5\uc77c \ubb38\ud559\uc758 \uac70\uc7a5\uc774\uc790 \ub178\ubca8\ubb38\ud559\uc0c1 \uc218\uc0c1\uc791\uac00 \ud5e4\ub974\ub9cc \ud5e4\uc138\uc758 \uc790\uc804\uc801 \uc18c\uc124\uc774\ub2e4. 1\ucc28 \uc138\uacc4\ub300\uc804 \uc9c1\ud6c4\uc778 1919\ub144 \uc5d0\ubc00 \uc2f1\ud074\ub808\uc5b4\ub77c\ub294 \uac00\uba85\uc73c\ub85c \ubc1c\ud45c\ud588\ub358 \uc791\ud488\uc73c\ub85c, \uc5f4 \uc0b4 \uc18c\ub144\uc774 \uc2a4\ubb34 \uc0b4 \uccad\ub144\uc774 \ub418\uae30\uae4c\uc9c0 \uace0\ub3c5\ud558\uace0 \ud798\ub4e0 \uc131\uc7a5\uc758 \uacfc\uc815\uc744 \uadf8\ub9ac\uace0 \uc788\ub2e4. \ubd88\uc548\uacfc \uc88c\uc808\uc5d0 \uc0ac\ub85c\uc7a1\ud78c \uccad\ucd98\uc758 \ub0b4\uba74\uc744 \ub2e4\ub8ec \uc774 \uc791\ud488\uc740 \uc9c0\uae08\uae4c\uc9c0 \uc218\ub9ce\uc740 \uccad\ub144\uc138\ub300\uc758 \ub9c8\uc74c\uc5d0 \uae4a\uc740 \uc6b8\ub9bc\uc744 \uc804\ud558\uace0 \uc788\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000201096967"},{"imgSrc":"../images/novel/2.jpg","title":"\uc791\ubcc4\ud558\uc9c0 \uc54a\ub294\ub2e4","author":"\ud55c\uac15","publicationDate":"2021. 09. 09","description":"\ubd80\ucee4\uc0c1 \uc218\uc0c1 \uc774\ud6c4 5\ub144, \ud55c\uac15 \uc18c\uc124\uc758 \ud604\uc7ac","description2":"\ud55c\uac15\uc744 \uc0ac\ub791\ud558\ub294 \ub3c5\uc790\uc758 \uc785\uc7a5\uc744 \ub5a0\ub098, \uc11c\uc810 \uc9c1\uc6d0\uc758 \uc785\uc7a5\uc5d0\uc11c\ub3c4 \ub9e4 \ud574\ub9c8\ub2e4 \uc18c\uc124\uac00 \ud55c\uac15\uc758 \uc2e0\uc791\uc744 \uae30\ub2e4\ub838\ub2e4. \ud669\uc21c\uc6d0\ubb38\ud559\uc0c1\uc744 \uc218\uc0c1\ud55c <\ub208 \ud55c \uc1a1\uc774\uac00 \ub179\ub294 \ub3d9\uc548>, \uae40\uc720\uc815\ubb38\ud559\uc0c1\uc744 \uc218\uc0c1\ud55c <\uc791\ubcc4>\uc744 \ud568\uaed8 \uc5ee\uc5b4 \ucd9c\uac04\ub420 \'\ub208 3\ubd80\uc791\'\uc758 \ubb3c\uc131\uc744 \uc0c1\uc0c1\ud558\uba70. \ubcf8\ub798 3\ubd80\uc791\uc758 \ub9c8\uc9c0\ub9c9 \uc774\uc57c\uae30\ub85c \ub193\uc77c \uac83\uc774\uc5c8\ub358 \uc774\uc57c\uae30, <\uc791\ubcc4\ud558\uc9c0 \uc54a\ub294\ub2e4>\uac00 (3\ubd80\uc791\uc758 \ub450\ubc88\uc9f8 \uc774\uc57c\uae30\uac00 \'\uc791\ubcc4\'\uc774\uc5c8\ub2e4\ub294 \uc810\ub3c4 \uc758\ubbf8\uc2ec\uc7a5\ud558\uac8c \uc77d\ud78c\ub2e4.) \ub3c5\ub9bd\ub41c \uc774\uc57c\uae30\ub85c \ub4dc\ub514\uc5b4 \ub3c5\uc790\ub97c \ucc3e\uc558\ub2e4. \ud55c\uac15\uc758 \uc18c\uc124\uc744 \uc0ac\ub791\ud55c \ub3c5\uc790\ub77c\uba74 \uccab \ubb38\uc7a5\uc744 \uc77d\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \uadf8\uc758 \ub2e4\uc815\ud558\uace0 \uc870\uc2ec\uc2a4\ub7ec\uc6b4 \uc74c\uc0c9\uc774 \uc0c1\uc0c1\ub420 \ubc95\ud55c \uc2dc\uc801\uc778 \ud48d\uacbd\uc73c\ub85c, \ub208\ubcf4\ub77c\uac00 \uce5c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000781116"},{"imgSrc":"../images/novel/3.jpg","title":"\ubaa8\uc21c","author":"\uc591\uadc0\uc790","publicationDate":"2013.04.01","description":"\uc778\uc0dd\uc740 \uc0b4\uc544\uac00\uba74\uc11c \ud0d0\uad6c\ud558\ub294 \uac83!","description2":"\uc774 \uc791\ud488\uc774 \ucd9c\uac04\ub41c \uc9c0 15\ub144, \ubaa8\uc21c\uc73c\ub85c \uc5bd\ud78c \uc0b6\uc740 \uc5ec\uc804\ud788 \uc5b4\ub835\uae30\ub9cc \ud558\uc9c0\ub9cc \uc778\uc0dd\uc744 \uadf8\ub0e5 \ud758\ub824\ubcf4\ub0b4\uace0 \uc788\ub294 \ub9ce\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uc0c8\ub86d\uac8c \uc778\uc0dd\uc744 \ub3cc\uc544\ubcf4\ub294 \uacc4\uae30\ub97c \ub9c8\ub828\ud574\uc900\ub2e4. \uc77c\uc0c1\uc758 \uc9c0\uadf9\ud788 \uc0ac\uc18c\ud55c \uc5d0\ud53c\uc18c\ub4dc\ub4e4\uc744 \uc120\ubcc4\ud574 \ub4e4\ub824\uc8fc\uba70, \uc778\uc0dd\uc740 \uadf8\ub0e5 \ubc1b\uc544\ub4e4\uc774\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ud0d0\uad6c\ud558\uba74\uc11c \uc0b4\uc544\uc57c \ud558\ub294 \uac83\uc784\uc744 \uc774\uc57c\uae30\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001632467"},{"imgSrc":"../images/novel/4.jpg","title":"\uc778\uac04 \uc2e4\uaca9","author":"\ub2e4\uc790\uc774 \uc624\uc0ac\ubb34","publicationDate":"2004.05.15","description":"\uccad\ucd98\uc758 \ud55c \uc2dc\uae30\ub97c \ud1b5\uacfc \uc758\ub840\ucc98\ub7fc \uac70\uccd0\uc57c \ud558\ub294 \uc77c\ubcf8 \ub370\uce74\ub2f9\uc2a4 \ubb38\ud559\uc758 \ub300\ud45c\uc791","description2":"\uc624\uc9c1 \uc21c\uc218\ud568\ub9cc\uc744 \uac08\ub9dd\ud558\ub358 \uc5ec\ub9b0 \uc2ec\uc131\uc758 \ud55c \uc80a\uc740\uc774\uac00 \uc778\uac04\ub4e4\uc758 \uc704\uc120\uacfc \uc794\uc778\ud568\uc5d0 \uc758\ud574 \ud30c\uba78\ub418\uc5b4 \uac00\ub294 \uacfc\uc815\uc744 \uadf8\ub9b0 \uc18c\uc124.1948\ub144 \uc11c\ub978\uc544\ud649\uc758 \ub098\uc774\ub85c \uc694\uc808\ud558\uc5ec \uc77c\ubcf8 \uc0ac\ud68c\uc5d0 \ud070 \ud30c\uc7a5\uc744 \ub0a8\uae34 \uc77c\ubcf8 \ud604\ub300 \ubb38\ud559\uc758 \ub300\ud45c\uc801 \uc791\uac00 \ub2e4\uc790\uc774 \uc624\uc0ac\ubb34\uc758 \uc791\ud488\uc774\ub2e4. \uc5b4\ub5bb\uac8c\ub4e0 \uc0ac\ud68c\uc5d0 \uc735\ud654\ub418\uace0\uc790 \uc560\uc4f0\uace0, \uc21c\uc218\ud55c \uac83, \ub354\ub7fd\ud600\uc9c0\uc9c0 \uc54a\uc740 \uac83\uc5d0 \uafc8\uc744 \uc758\ud0c1\ud558\uace0, \uc778\uac04\uc5d0 \ub300\ud55c \uad6c\uc560\ub97c \uc2dc\ub3c4\ud558\ub358 \uc8fc\uc778\uacf5\uc774 \uacb0\uad6d \ubaa8\ub4e0 \uac83\uc5d0 \ubc30\ubc18\ub2f9\ud558\uace0 \uc778\uac04 \uc2e4\uaca9\uc790\uac00 \ub418\uc5b4\uac00\ub294 \ud328\ubc30\uc758 \uae30\ub85d\uc744 \ud1b5\ud574 \ud604\ub300 \uc0ac\ud68c\ub97c \uc608\ub9ac\ud55c \uace0\ubc1c\ud558\uace0 \uc788\ub2e4. \ud568\uaed8 \uc2e4\ub9b0 \'\uc9c1\uc18c\'\uc5d0\uc11c\ub294 \uc720\ub2e4\uc758 \uc778\uac04\uc801\uc778 \uce21\uba74\uc744 \uc800\uc790\ub9cc\uc758 \ub3c5\ud2b9\ud55c \uc2dc\uc120\uc73c\ub85c \uc0c8\ub85c\uc774 \uc870\uba85\ud558\uace0 \uc788\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000620240"},{"imgSrc":"../images/novel/5.jpg","title":"\ub2ec\ub7ec\uad6c\ud2b8 \uafc8 \ubc31\ud654\uc810","author":"\uc774\ubbf8\uc608","publicationDate":"2020.07.08","description":"\uc7a0\ub4e4\uc5b4\uc57c\ub9cc \uc785\uc7a5 \uac00\ub2a5\ud55c \uafc8 \ubc31\ud654\uc810\uc5d0\uc11c \uc77c\uc5b4\ub098\ub294 \ube44\ubc00\uc2a4\ub7fd\uace0\ub3c4 \uae30\ubb18\ud558\uba70 \uac00\uc2b4 \ubb49\ud074\ud55c \ud310\ud0c0\uc9c0 \uc18c\uc124","description2":"\uc5ec\uae30\ub294 \uc7a0\ub4e4\uc5b4\uc57c\ub9cc \uc785\uc7a5\ud560 \uc218 \uc788\ub294 \u2018\ub2ec\ub7ec\uad6c\ud2b8 \uafc8 \ubc31\ud654\uc810\u2019\uc785\ub2c8\ub2e4. \uc7a0\ub4e4\uc5b4\uc57c\ub9cc \uc785\uc7a5\ud560 \uc218 \uc788\ub294 \ub3c5\ud2b9\ud55c \ub9c8\uc744. \uadf8\uacf3\uc5d0 \ub4e4\uc5b4\uc628 \uc7a0\ub4e0 \uc190\ub2d8\ub4e4\uc5d0\uac8c \uac00\uc7a5 \uc778\uae30 \uc788\ub294 \uacf3\uc740, \uc628\uac16 \uafc8\uc744 \ud55c\ub370 \ubaa8\uc544 \ud310\ub9e4\ud558\ub294 \u2018\ub2ec\ub7ec\uad6c\ud2b8\uc758 \uafc8 \ubc31\ud654\uc810\u2019\uc774\ub2e4. \uae34 \uc7a0\uc744 \uc790\ub294 \uc0ac\ub78c\ub4e4\uc740 \ubb3c\ub860\uc774\uace0, \uc9e7\uc740 \ub0ae\uc7a0\uc744 \uc790\ub294 \uc0ac\ub78c\ub4e4\uacfc \ub3d9\ubb3c\ub4e4\ub85c \ub9e4\uc77c\ub9e4\uc77c \ub300\uc131\ud669\uc744 \uc774\ub8ec\ub2e4. \ubc94\uc0c1\uce58 \uc54a\uc740 \ud608\ud1b5\uc758 \uc8fc\uc778\uc7a5 \u2018\ub2ec\ub7ec\uad6c\ud2b8\u2019, \uadf8\ub9ac\uace0 \uadf8\uc758 \ucd5c\uce21\uadfc\uc5d0\uc11c \uc77c\ud558\uac8c \ub41c \uc2e0\ucc38 \uc9c1\uc6d0 \u2018\ud398\ub2c8\u2019, \uafc8\uc744 \ub9cc\ub4dc\ub294 \uc81c\uc791\uc790 \u2018\uc544\uac00\ub135 \ucf54\ucf54\u2019, \uadf8\ub9ac\uace0 \ubca0\uc77c\uc5d0 \ub458\ub7ec\uc2f8\uc778 \ube44\uace0 \ub9c8\uc774\uc5b4\uc2a4\u2026\ub4f1\uc774 \ub4f1\uc7a5\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001835614"},{"imgSrc":"../images/novel/6.jpg","title":"\ubaa8\ube44\ub515","author":"\ud5c8\uba3c \uba5c\ube4c","publicationDate":"2022.09.02","description":"\uc808\ub300\uc801 \uc9c4\ub9ac\ub9cc\uc744 \uac15\uc694\ud558\ub358 \ud3ed\ub825\uc758 \uc2dc\ub300\uc5d0 \ub9de\uc11c \uc2e4\ud5d8\uc801\uc774\uace0 \uc804\uc704\uc801\uc778 \ubb38\ud559\uc758 \ud6a8\uc2dc\uac00 \ub41c \ubd88\ud6c4\uc758 \uace0\uc804","description2":"1851\ub144\uc5d0 \ucd9c\uac04\ub41c \u300e\ubaa8\ube44 \ub515\u300f\uc740 \uc774\ubbf8 \ubc18\uc138\uae30 \uc55e\uc11c 20\uc138\uae30\uc5d0 \ub3c4\ub798\ud560 \ubaa8\ub354\ub2c8\uc998\uc744 \uc608\uace0\ud588\ub2e4. \uc138\uc0c1 \ubaa8\ub4e0 \uc9c4\ub9ac\ub97c \uc548\ub2e4\ub294 \ub4ef \uc2e0\uc758 \uc704\uce58\uc5d0\uc11c \uc18c\uc124\uc744 \uc368 \ub0b4\ub824\uac04 19\uc138\uae30 \ub9ac\uc5bc\ub9ac\uc998 \uc18c\uc124\uac00\ub4e4\uacfc\ub294 \ub2ec\ub9ac, 20\uc138\uae30 \ubaa8\ub354\ub2c8\uc998 \uc18c\uc124\uac00\ub4e4\uc740 \uc138\uc0c1\uc744 \ubc14\ub77c\ubcf4\ub294 \ud654\uc790\uc758 \uc8fc\uad00\uc801 \uad00\uc810\uacfc \ub0b4\uba74 \uc2ec\ub9ac\ub97c \uadf9\ud654\ud558\ub294 \ub370 \uc9d1\uc911\ud588\ub2e4. \uadf8\ub9ac\ud558\uc5ec \u300e\ubaa8\ube44 \ub515\u300f\uc740 \ud68d\uae30\uc801\uc778 \ud4e8\uc804\ud48d \uc2a4\ud1a0\ub9ac\ud154\ub9c1, \ub3c5\ucc3d\uc801\uc778 \uc791\ud488 \uad6c\uc870, \ub2e4\uc591\ud55c \uc778\uac04 \uad70\uc0c1 \ucd94\uc801, \uc774\uc57c\uae30\uc640 \uc0c1\uc9d5\uc758 \uc808\ubb18\ud55c \uacb0\ud569, \uc778\uc0dd\uc758 \uc2e0\ube44\ub97c \ub458\ub7ec\uc2fc \uae4a\uc740 \uc885\uad50\uc801\xb7\ucca0\ud559\uc801 \ud0d0\uad6c, \ub6f0\uc5b4\ub09c \uc720\uba38 \uac10\uac01\uacfc \ud48d\uc790, \uc5f4\ub9b0 \uacb0\ub9d0 \ub4f1\ub4f1 \uae30\uc874\uc5d0 \uc5c6\ub358 \uc2e4\ud5d8\uc801\uc774\uace0 \uc804\uc704\uc801\uc778 \ud615\uc2dd\uc73c\ub85c \ubbf8\uad6d \ubaa8\ub354\ub2c8\uc998 \ubb38\ud559\uc758 \ud6a8\uc2dc\uc774\uc790 \uc0c1\uc9d5\uc8fc\uc758 \ubb38\ud559\uc758 \ub300\ud45c\uc791\uc774 \ub418\uc5c8\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000061694904"}]')}}]);