"use strict";(self.webpackChunkwrapkit=self.webpackChunkwrapkit||[]).push([[171],{171:i=>{i.exports=JSON.parse('[{"imgSrc":"../images/religion/1.jpg","title":"\ud55c\uad6d \uad50\ud68c \ud2b8\ub80c\ub4dc 2024","author":"\uc9c0\uc6a9\uadfc","publicationDate":"2023.09.19","description":"\uc815\ud655\ud55c \uc870\uc0ac \ub370\uc774\ud130\uc5d0 \uadfc\uac70\ud55c \uad50\ud68c \uc218\ucd95\uc758 \uc2dc\ub300 2024\ub144 \ud55c\uad6d \uad50\ud68c \uc804\ub9dd\uacfc \uc804\ub7b5","description2":"\u201c\ud750\ub984\uc744 \uc77d\ub294 \uad50\ud68c\uac00 \uc0b4\uc544\ub0a8\ub294\ub2e4!!\u201d \ud55c\uad6d \uad50\ud68c\ub294 \uc9c0\uae08 \uc0b4\uc544\ub0a8\uc744 \uc218 \uc788\uc744\uae4c? \ud55c\uad6d \uad50\ud68c \uc0dd\uc874 \uc804\ub7b5 \ud2b8\ub80c\ub4dc \uc815\ubc00 \ubd84\uc11d\uc11c","link":"https://product.kyobobook.co.kr/detail/S000208996994"},{"imgSrc":"../images/religion/2.jpg","title":"\ubc95\ub95c \uc2a4\ub2d8\uc758 \ubc18\uc57c\uc2ec\uacbd \uac15\uc758","author":"\ubc95\ub95c","publicationDate":"2022.06.30","description":"\ubd88\uad50\uc758 \ud575\uc2ec\uc0ac\uc0c1\uc744 \ub300\uc911 \ub208\ub192\uc774\ub85c \ud480\uc5b4\ub0b8 \uc5ed\uc791!","description2":"2022\ub144 6\uc6d4, \uc644\uc804\ud788 \uc0c8\ub86d\uac8c \ucd9c\uac04\ub41c \u300e\ubc95\ub95c \uc2a4\ub2d8\uc758 \ubc18\uc57c\uc2ec\uacbd \uac15\uc758\u300f\ub294 \ubd88\uad50\ub97c \uc804\ud600 \ubaa8\ub974\ub294 \uc0ac\ub78c\ub4e4\ub3c4 \uc774\ud574\ud560 \uc218 \uc788\uc744 \ub9cc\ud07c \uc2ec\uc624\ud55c \ubd88\uad50 \uc0ac\uc0c1\uc744 \uccb4\uacc4\uc801\uc774\uace0 \uacfc\ud559\uc801\uc73c\ub85c \ub354\uc6b1\ub354 \uc27d\uace0 \uba85\ucf8c\ud558\uac8c \ud480\uc5b4\ub0c8\ub2e4. \ubd80\ucc98\ub2d8 \ub9d0\uc500\uc774 \uc774\ub807\uac8c \uc774\ud574\ud558\uae30 \uc26c\uc6b4 \uac83\uc774\uc5c8\ub2e4\ub294 \uc810\uc5d0 \ub204\uad6c\ub098 \ub180\ub77c\uac8c \ub420 \uac83\uc774\ub2e4. \uadf8\ub9ac\uace0 2600\ub144 \uc804, \ubd80\ucc98\ub2d8\uc774 \uad34\ub85c\uc6c0\uc758 \uc6d0\uc778\uc774 \u2018\ub9c8\uc74c\u2019\uc5d0 \uc788\ub2e4\ub294 \uac83\uacfc \uadf8 \uad34\ub85c\uc6c0\uc744 \ud574\uacb0\ud558\ub294 \ubc29\ubc95\uc744 \uc644\ubcbd\ud788 \uc81c\uc2dc\ud574 \uc8fc\uc5c8\ub2e4\ub294 \ub370 \uae4a\uc774\uae4a\uc774 \uacbd\ud0c4\ud560 \uac83\uc774\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000061353499"},{"imgSrc":"../images/religion/3.jpg","title":"\ud558\ub098\ub2d8, \uc800 \uc798 \uc0b4\uace0 \uc788\ub098\uc694?","author":"\uc804\ub300\uc9c4","publicationDate":"2022.02.10","description":"\ucda9\ubd84\ud788 \ube5b\ub098\uace0 \uc788\ub294 \ub2f9\uc2e0\uc744 \uc704\ud55c \uc77c\uc0c1 \ubb35\uc0c1","description2":"\uc774 \ucc45\uc744 \ud1b5\ud574 \uc6b0\ub9ac\uac00 \uadf8\uc5d0\uac8c \ubc30\uc6b8 \uc218 \uc788\ub294 \uac83\uc740 \ubb34\uc5c7\uc77c\uae4c? \uc6b0\ub9ac\uac00 \ud070 \uad50\ud1b5\uc0ac\uace0\ub97c \ub2f9\ud588\ub294\ub370 \ub2e4\uce5c \uacf3\uc774 \uc5c6\ub2e4\uace0 \uac00\uc815\ud574\ubcf4\uc790. \uac00\uc7a5 \uba3c\uc800 \ubb34\uc5c7\uc744 \ud560\uae4c? \ud558\ub098\ub2d8\uaed8 \uac10\uc0ac\ud560 \uac83\uc774\ub2e4. \ub300\ubd80\ubd84 \uac70\uae30\uc11c \uadf8\uce58\uc9c0 \uc54a\uc744\uae4c? \uadf8 \uc0c1\ud669\uc5d0\uc11c \uc800\uc790\ub294 \ubaa9\uc0ac\ub2d8\uaed8 \uac10\uc0ac\uc608\ubc30\ub97c \uc694\uccad \ub4dc\ub9b0\ub2e4. \ud070 \uc0ac\uace0\uc600\uc9c0\ub9cc \ubab8\uc5d0 \uc544\ubb34 \uc774\uc0c1\uc774 \uc5c6\uc5c8\uace0, \uc0ac\uace0\ub97c \ud1b5\ud574 \ub354 \uacb8\uc190\ud558\uace0 \uc9c4\uc2e4\ub41c \ub9c8\uc74c\uc73c\ub85c \uc0b4\uc544\uac00\ub3c4\ub85d \ub2e4\uc2dc \ud55c \ubc88 \uc2a4\uc2a4\ub85c\ub97c \ub3cc\uc544\ubcfc \uc218 \uc788\ub294 \uae30\ud68c\ub97c \uc8fc\uc2e0 \ud558\ub098\ub2d8\uaed8 \uac10\uc0ac\ud588\uae30 \ub54c\ubb38\uc774\ub2e4. \uacf3\uacf3\uc5d0 \ud558\ub098\ub2d8\uc744 \ub300\ud558\ub294 \uc774\ub7f0 \uc801\uadf9\uc801\uc778 \uc0b6\uc758 \ud0dc\ub3c4\ub4e4\uc774 \uc6b0\ub9ac\uc758 \uc2e0\uc559\uc744 \ub3cc\uc544\ubcf4\uac8c \ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001848031"},{"imgSrc":"../images/religion/4.jpg","title":"\ucc57GPT, \ubaa9\ud68c\uc790\ub294 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud560\uae4c?(\ubaa9\ud68c\uc790\uc6a9)","author":"\uc11c\uacbd\uc6d0","publicationDate":"2023.10.30","description":"\ucc57GPT\ub85c \ub9de\ucda4\ud615 \ubaa9\ud68c \ube44\uc11c \ub9cc\ub4e4\uae30","description2":"\ud61c\uc131\uacfc \uac19\uc774 \ub4f1\uc7a5\ud574 \uc804 \uc138\uacc4\ub97c \ub180\ub77c\uac8c \ud588\ub358 \ucc57GPT. \uc0ac\ub78c\ub4e4\uc740 \uc778\uacf5\uc9c0\ub2a5\uc758 \uc5ed\ud560\uc774 \ubaa9\ud68c\uc790\uc758 \uc601\uc5ed\ub3c4 \ub300\uccb4\ud560 \uac83\uc774\ub77c\uace0 \uc608\uce21\ud55c\ub2e4. \uc815\ub9d0 \uc778\uacf5\uc9c0\ub2a5\uc774 \uc131\ub3c4\ub4e4\uc5d0\uac8c \uc124\uad50\ud558\uace0 \ubaa9\uc591\ud558\ub294 \ub0a0\uc774 \uc62c\uae4c? \ub300\ud559\uc6d0\uc5d0\uc11c \uc778\uacf5\uc9c0\ub2a5\uc744 \uc5f0\uad6c\ud55c \uacf5\ud559\ub3c4 \ucd9c\uc2e0\uc758 \uc800\uc790\ub294 \uc774\uc5d0 \ub300\ud574 \uacfc\uc7a5\ub41c \uc6b0\ub824\ub77c\uace0 \ub2f5\ud55c\ub2e4. \uc778\uacf5\uc9c0\ub2a5\uc740 \ub3c4\uad6c\uc77c \ubfd0, \uc644\ubcbd\ud55c \uc124\uad50\ubb38\uc744 \uc791\uc131\ud558\uc9c0 \ubabb\ud558\uba70 \uc124\uad50\uc758 \uc911\uc2ec\uc740 \uc5ec\uc804\ud788 \ubaa9\ud68c\uc790\uc758 \uc2e0\uc559\uacfc \ud1b5\ucc30\uc5d0 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \ud558\uc9c0\ub9cc \ube60\ub974\uace0 \ub611\ub611\ud55c \ucc57GPT\ub97c \uae30\ub2a5\uc801\uc73c\ub85c\ub9cc \uc798 \uc0ac\uc6a9\ud55c\ub2e4\uba74, \ubaa9\ud68c\uc790\uc758 \uc5c5\ubb34 \uacfc\uc911\uc744 \uc904\uc5ec \uc624\ud788\ub824 \ubaa9\ud68c\uc790\uac00 \ubcf8\uc9c8\uc801\uc778 \uc0ac\uc5ed\uc5d0 \uc9d1\uc911\ud558\ub294 \ub370 \ub3c4\uc6c0\uc744 \uc904 \uc218 \uc788\ub2e4\uace0 \uc124\uba85\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000210724076"},{"imgSrc":"../images/religion/5.jpg","title":"\ub0b4\uba74\uc138\uacc4\uc758 \uc9c8\uc11c\uc640 \uc601\uc801 \uc131\uc7a5","author":"\uace0\ub4e0 \ub9e5\ub3c4\ub0a0\ub4dc","publicationDate":"2018.09.10","description":"\ub0b4\uba74\uc744 \ubc14\ub85c\uc7a1\uc744 \ub54c \ud558\ub098\ub2d8\uc758 \uc9c8\uc11c\uac00 \ub0b4\uac8c \uc784\ud55c\ub2e4!","description2":"\u2018\ub0b4\uba74\uc138\uacc4\uc758 \uc9c8\uc11c\u2019\ub77c\ub294 \ud68d\uae30\uc801\uc778 \ubb38\uc81c\ub97c \uc81c\uae30\ud558\uba74\uc11c \uc218\ub9ce\uc740 \uadf8\ub9ac\uc2a4\ub3c4\uc778\ub4e4\uc744 \ub3c4\uc804\ud558\uace0 \ubcc0\ud654\uc2dc\ud0a8 \ucc45. \ucad3\uaca8\ub2e4\ub2c8\ub294 \uc0b6\uc5d0 \ub300\ud55c \uadfc\ubcf8\uc801 \ud574\uacb0\ucc45\uc73c\ub85c \ub0b4\uba74\uc138\uacc4\uc758 \uc9c8\uc11c\ub97c \uac15\uc870\ud558\ub294 \uc774 \ucc45\uc740 \uad6d\ub0b4\uc5d0\uc11c 1990\ub144 \ucd08\ud310\uc744 \uc120\ubcf4\uc778 \uc774\ub798 40\ub9cc \ubd80 \uac00\ub7c9 \ud310\ub9e4\ub41c \uc2a4\ud14c\ub514\uc140\ub7ec\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000579664"},{"imgSrc":"../images/religion/6.jpg","title":"\uc778\uac04 \ubd93\ub2e4 \uadf8 \uc704\ub300\ud55c \uc0b6\uacfc \uc0ac\uc0c1","author":"\ubc95\ub95c","publicationDate":"2010.03.15","description":"\ubd93\ub2e4\uac00 \uc9c0\uae08 \uc774 \ub545\uc5d0 \uc624\uc2e0\ub2e4\uba74 \uc5b4\ub5bb\uac8c \uc0b4\uc544\uac00\uc2e4\uae4c?","description2":"\uac08\ub4f1\uacfc \ubd84\uc7c1\uc774 \uc788\ub294 \uacf3\uc5d0 \ud3c9\ud654\ub97c \uc2e4\ud604\ud558\ub294 \ud3c9\ud654\uc6b4\ub3d9\uac00 \ubc95\ub95c \uc2a4\ub2d8\uc758 \u300e\uc778\uac04 \ubd93\ub2e4, \uadf8 \uc704\ub300\ud55c \uc0b6\uacfc \uc0ac\uc0c1\u300f. 2,600\uc5ec \ub144 \uc804 \ubd93\ub2e4\uc758 \uc0b6\uc744 \uc778\uac04\uc758 \uc5ed\uc0ac\ub85c \uc7ac\uc870\uba85\ud55c\ub2e4. \ubd93\ub2e4\uc758 \uc704\ub300\ud568\uc744 \ucc2c\ud0c4\ud558\uae30\ubcf4\ub2e4\ub294, \uadf8\ubd84\uc758 \uc0b6\uc744 \ud1b5\ud574 \uc624\ub298\uc744 \uc0b4\uc544\uac00\ub294 \uc6b0\ub9ac \uc0b6\uc758 \ubc29\ud5a5\uc744 \uc810\uac80\ud558\uace0 \uc788\ub2e4. \ud2b9\ud788 \'\ubd93\ub2e4\uac00 \uc9c0\uae08 \uc774 \ub545\uc5d0 \uc624\uc2e0\ub2e4\uba74 \uc5b4\ub5bb\uac8c \uc0b4\uc544\uac00\uc2e4\uae4c?\'\ub77c\ub294 \ubb3c\uc74c\uc5d0 \ub300\ub2f5\uc744 \ub358\uc9c4\ub2e4. \ubd93\ub2e4\uac00 \uac78\uc5b4\uac00\uc2e0 \uc0b6\uc774 \uc9c0\ub2cc \ubcf8\uc9c8\uc801 \uc758\ubbf8\ub97c \ucc3e\uc544\ub0b4, \uc6b0\ub9ac \uc0b6\uc744 \ub458\ub7ec\uc2fc \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \ubc29\uc548\uc744 \ubaa8\uc0c9\ud558\uace0 \uc788\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001351322"}]')}}]);