"use strict";(self.webpackChunkwrapkit=self.webpackChunkwrapkit||[]).push([[203],{203:i=>{i.exports=JSON.parse('[{"imgSrc":"../images/humanities/1.jpg","title":"\uc0ac\ud53c\uc5d4\uc2a4","author":"\uc720\ubc1c \ud558\ub77c\ub9ac","publicationDate":"2015.11.24","description":"\uc778\uac04\uc758 \uc5ed\uc0ac\uc640 \ubbf8\ub798\uc5d0 \ub300\ud55c \uac00\uc7a5 \ub17c\uc7c1\uc801\uc774\uace0 \ub300\ub2f4\ud55c \ub300\uc11c\uc0ac","description2":"\u2018\ucc9c\uc7ac \uc0ac\uc0c1\uac00\u2019(\ub274\uc695\ud0c0\uc784\uc2a4) \uc720\ubc1c \ud558\ub77c\ub9ac\uc758 \ub300\ud45c\uc791 \u300a\uc0ac\ud53c\uc5d4\uc2a4\u300b. \uc774\uc81c \ubd88\ud669\uc774\ub77c\ub294 \uc218\uc2dd\uc5b4\uac00 \uc804\ud600 \uc5b4\uc0c9\ud558\uc9c0 \uc54a\uc740 \uad6d\ub0b4 \ucd9c\ud310\uc2dc\uc7a5\uc5d0\uc11c\ub3c4 \u300a\uc0ac\ud53c\uc5d4\uc2a4\u300b\ub294 \uc778\ubb38\uad50\uc591 \ubd84\uc57c\uc758 \ud2b8\ub80c\ub4dc\ub97c \uc8fc\ub3c4\ud558\uba70 2023\ub144 1\uc6d4 \uae30\uc900 \u2018200\uc1c4 \ubc1c\ud589\xb7115\ub9cc\ubd80 \ud310\ub9e4\u2019\ub77c\ub294 \ub180\ub77c\uc6b4 \uae30\ub85d\uc744 \uac70\ub450\uace0 \uc788\ub2e4. \uc778\ub958 \uc5ed\uc0ac\uc640 \ubbf8\ub798\ub97c \uc885\ud6a1\ubb34\uc9c4 \uac00\ub85c\uc9c0\ub974\ub294 \u300a\uc0ac\ud53c\uc5d4\uc2a4\u300b\uc758 \ud1b5\ucc30\uc740 \ubd88\ud655\uc2e4\ud558\uace0 \ubcf5\uc7a1\ud55c \uc138\uacc4\ub97c \uc774\ud574\ud558\uace0 \ub300\ube44\ud558\ub294 \ub370 \ubc18\ub4dc\uc2dc \ud544\uc694\ud558\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000597165"},{"imgSrc":"../images/humanities/2.jpg","title":"\uc9c0\uc801 \ub300\ud654\ub97c \uc704\ud55c \ub113\uace0 \uc595\uc740 \uc9c0\uc2dd 1","author":"\ucc44\uc0ac\uc7a5","publicationDate":"2020.02.05","description":"\uc218\ubc31\ub9cc \ub3c5\uc790\uac00 \uc77d\uace0 \uc5f4\uad11\ud558\uace0, \uc218\ub144\uac04 \uc778\ubb38\ud559 \ud544\uc218 \ub3c4\uc11c\ub85c \uc790\ub9ac \uc7a1\uc740 \ub300\ud45c \uc778\ubb38\ud559 \uc2dc\ub9ac\uc988\uac00 \uc637\uc744 \uac08\uc544\uc785\uace0 \ub3cc\uc544\uc654\ub2e4","description2":"\uc5b4\ub835\uace0 \ub531\ub531\ud558\ub358 \uc778\ubb38\ud559 \ubd84\uc57c \ud310\ub3c4\ub97c \ub4a4\ubc14\uafbc \ucc45. \ucd9c\uac04 \uc989\uc2dc \ubca0\uc2a4\ud2b8\uc140\ub7ec\uc5d0 \uc624\ub974\ub294 \uac83\uc744 \uc2dc\uc791\uc73c\ub85c, 200\ub9cc \ubd80 \ub204\uc801 \ud310\ub9e4\ub97c \ub3cc\ud30c\ud574 \ub354\ube14 \ubc00\ub9ac\uc5b8\uc140\ub7ec\ub97c \uae30\ub85d\ud558\uba70, \uc7a5\uc7a5 5\ub144\uac04 \uc2a4\ud14c\ub514\uc140\ub7ec\uc758 \uc790\ub9ac\uc5d0\uc11c \uafc8\uca4d\ub3c4 \ud558\uc9c0 \uc54a\uc740 \ucc45. \uae30\ucd08 \uc0c1\uc2dd\uc5d0 \ubaa9\ub9d0\ub77c \uc788\ub358 \ubcf4\ud1b5 \uc0ac\ub78c\ub4e4\uc774 \uac70\ub9ac\ub08c \uc5c6\uc774 \ud1a0\ub860\ud558\uac8c \ud558\uace0 \ub274\uc2a4\ub97c \uc8fc\uccb4\uc801\uc73c\ub85c \ubcf4\uac8c \ud558\uace0 \uc120\uac70\uc5d0\uc11c \uc8fc\uad00\uc744 \uac16\uac8c \ud55c \ucc45, 80\ub300 \ub3c5\uc790\uac00 \ub3c5\ud559\uc744 \uc2dc\uc791\ud558\uace0 \uc911\ud559\uc0dd \ub3c5\uc790\uac00 \uc778\ubb38\ud559\uc744 \uc77d\uac8c \ud558\uace0 \uc9c1\uc7a5\uc778\ub4e4\uc774 \ub3c5\uc11c \ubaa8\uc784\uc744 \uac16\uac8c \ud558\ub294 \ub4f1 \uc138\ub300 \ubd88\ubb38 \ub0a8\ub140\ub178\uc18c \uc77d\uc744 \uc218 \uc788\ub3c4\ub85d \uc27d\uace0 \uc7ac\ubbf8\uc788\uac8c \uc4f0\uc600\ub2e4\uace0 \ud3c9\uac00\ubc1b\ub294 \ucc45. \uc774 \ubaa8\ub4e0 \uac83\uc774 \u300a\uc9c0\uc801 \ub300\ud654\ub97c \uc704\ud55c \ub113\uace0 \uc595\uc740 \uc9c0\uc2dd\u300b\uc5d0 \ub300\ud55c \uc124\uba85\uc774\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001938774"},{"imgSrc":"../images/humanities/3.jpg","title":"\uc815\uc758\ub780 \ubb34\uc5c7\uc778\uac00","author":"\ub9c8\uc774\ud074 \uc0cc\ub378","publicationDate":"2014.11.20","description":"\uc815\uc758\ub860\uc758 \uc138\uacc4\uc801 \uc11d\ud559, \ub9c8\uc774\ud074 \uc0cc\ub378 \uad50\uc218\uc758 \uc0dd\uae30 \ub118\uce58\ub294 \uac15\uc758\uc2e4\ub85c \ucd08\ub300\ud569\ub2c8\ub2e4!","description2":"\uc774 \ucc45\uc740 \uad6c\uc81c \uae08\uc735, \ubaa8\ubcd1\uc81c, \ub300\ub9ac \ucd9c\uc0b0\uacfc \uac19\uc740 \ud604\uc2e4 \ubb38\uc81c\ub97c \ube44\ub86f\ud574 \uacbd\ub85c\ub97c \uc774\ud0c8\ud55c \uc804\ucc28, \uace0\ud1b5\uc758 \ub300\uac00\ub97c \uacc4\ub7c9\ud558\ub294 \uc2dc\ud5d8\uacfc \uac19\uc740 \uc0ac\uace0 \uc2e4\ud5d8\uc744 \uc8fc\uc81c\ub85c \uc0bc\uc544, \uc704\ub300\ud55c \uc0ac\uc0c1\uac00\ub4e4\uc740 \u2018\uc815\uc758\u2019\uc5d0 \ub300\ud574 \uc5b4\ub5bb\uac8c \uc0dd\uac01\ud588\ub294\uc9c0 \ube44\ud310\uc801\uc73c\ub85c \uc0b4\ud3b4\ubcf8\ub2e4. \uac00\ub839, \uc800\uc790\ub294 \ubca4\ub2f4\uacfc \ubc00\uc758 \uacf5\ub9ac\uc8fc\uc758\ub294 \ub2e4\uc218\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub418\ub294 \uacb0\uc815\uc744 \uc9c0\uc9c0\ud558\uc9c0\ub9cc, \uc778\uac04\uc758 \uc874\uc5c4\uc131 \ubb38\uc81c\uc5d0\ub294 \ub3c4\ub355\uc801 \ud55c\uacc4\ub97c \uc9c0\ub2c8\uace0 \uc788\ub2e4\uace0 \ub9d0\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000625441"},{"imgSrc":"../images/humanities/4.jpg","title":"\ud329\ud2b8\ud480\ub2c8\uc2a4","author":"\ud55c\uc2a4 \ub85c\uc2ac\ub9c1, \uc62c\ub77c \ub85c\uc2ac\ub9c1, \uc548\ub098 \ub85c\uc2ac\ub9c1 \ub8b4\ub8ec\ub4dc","publicationDate":"2019.03.08","description":"\uac15\ub825\ud55c \uc0ac\uc2e4\uc744 \ubc14\ud0d5\uc73c\ub85c \uc138\uc0c1\uc744 \uc815\ud655\ud558\uac8c \ubc14\ub77c\ubcf4\ub294 \ubc29\ubc95\uc744 \ub2f4\uc740 \ud601\uba85\uc801 \uc800\uc791","description2":"\uc774 \ucc45\uc5d0\uc11c\ub294 \ub2e4\uc591\ud55c \ubd84\uc57c\ub4e4\uc774 \ub2e4\ub8e8\uace0 \uc788\ub294\ub370 \uadf9\ube48\uce35\uc758 \ube44\uc728, \uc5ec\uc131\uc758 \uad50\uc721\uae30\uac04, \uae30\ub300 \uc218\uba85, \uc790\uc5f0\uc7ac\ud574 \uc0ac\ub9dd\uc790 \uc218 \ub4f1 \ucd5c\uc2e0 \ud1b5\uacc4 \ub370\uc774\ud130\ub97c \ubc14\ud0d5\uc73c\ub85c \uc18c\uac1c\ub41c\ub2e4. \uc5b8\ub860 \ub4f1\uc5d0 \ud718\ub458\ub9ac\uc9c0 \uc54a\uace0 \uc815\ud655\ud55c \uc815\ubcf4\ub97c \uc5bb\ub294 \uac83\uc774 \uc5bc\ub9c8\ub098 \uc911\uc694\ud55c\uc9c0 \uac15\uc870\ud558\uba74\uc11c \uc6b0\ub9ac\uac00 \ubcf4\ud3b8\uc801\uc73c\ub85c \uacaa\uace0 \uc788\ub294 \ubd80\uc815\uc801\uc778 \uc2ec\ub9ac \ud574\uacb0\ucc45\ub3c4 \uc81c\uc2dc\ud588\ub2e4. \uc989 \uc5b4\ub5a4 \uc0ac\uac74\uc5d0 \ub300\ud574\uc11c \ud655\ub300\ud574\uc11d\ud558\uac70\ub098 \uad00\uc810\uc744 \uc65c\uace1\ud558\uc9c0 \uc54a\ub294 \ubc95\uc744 \ubc30\uc6b0\uac8c \ub420 \uac83\uc774\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000597791"},{"imgSrc":"../images/humanities/5.jpg","title":"\uacf5\uac04\uc774 \ub9cc\ub4e0 \uacf5\uac04","author":"\uc720\ud604\uc900","publicationDate":"2020.05.01","description":"\uc0c8\ub85c\uc6b4 \ubb38\ud654\ub294 \uc5b4\ub5bb\uac8c \ub9cc\ub4e4\uc5b4 \uc9c0\ub294\uac00?","description2":"\u300e\uacf5\uac04\uc774 \ub9cc\ub4e0 \uacf5\uac04\u300f\uc740 \uadf8\ub7f0 \uc9c0\uc5ed \uac04 \ubb38\ud654\uc758 \uad50\ub958\ub85c \uc0c8\ub85c\uc6b4 \uc0dd\uac01\uacfc \ubb38\ud654\uac00 \ub9cc\ub4e4\uc5b4\uc9c0\uace0, \ubd84\uc57c \uac04 \uc735\ud569\uc73c\ub85c \uc0c8\ub85c\uc6b4 \ubb38\ud654\uac00 \ud0c4\uc0dd\ud558\ub294 \ubb38\ud654 \uc720\uc804\uc790\uc758 \uc9c4\ud654\uc640 \uacc4\ubcf4\ub97c \uacf5\uac04\uc744 \uc911\uc2ec\uc73c\ub85c \uc0b4\ud3b4\ubcf8\ub2e4. \uc9c0\uae08\uc758 \ubb38\ud654 \uc720\uc804\uc790\uc758 \uc9c4\ud654 \ub2e8\uacc4\ub294 \uc5b4\ub514\uc774\uba70, \uc55e\uc73c\ub85c\ub294 \ubb34\uc5c7\uc774 \uc0c8\ub85c\uc6b4 \uac83\uc744 \ud0c4\uc0dd\uc2dc\ud0ac\uae4c? \uac74\ucd95\uc744 \uc911\uc2ec\uc73c\ub85c \uacfc\ud559, \uc5ed\uc0ac, \uc9c0\ub9ac \ub4f1 \ub2e4\uc591\ud55c \ubd84\uc57c\ub97c \uc544\uc6b0\ub974\uba70 \ubb38\ud654\uc758 \uae30\uc6d0\uacfc \ucc3d\uc870, \uad50\ub958, \ubcc0\uc885, \ubc1c\uc804\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\ub97c \uc800\uc790\ub294 \ud765\ubbf8\ub86d\uac8c \ud480\uc5b4\ub0b8\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000576557"},{"imgSrc":"../images/humanities/6.jpg","title":"\ub0b4\uac00 \uacf5\ubd80\ud558\ub294 \uc774\uc720","author":"\uc0ac\uc774\ud1a0 \ub2e4\uce74\uc2dc","publicationDate":"2014.06.16","description":"\uc77c\ubcf8 \uba54\uc774\uc9c0\ub300 \uad34\uc9dc \uad50\uc218\uc758 \uc778\uc0dd\uc744 \ubc14\uafb8\ub294 \ud3c9\uc0dd \uacf5\ubd80\ubc95","description2":"\u300e\ub0b4\uac00 \uacf5\ubd80\ud558\ub294 \uc774\uc720\u300f\ub294 \uc77c\ubcf8\uc5d0\uc11c \uc190\uaf3d\ud788\ub294 \uad50\uc721\uc2ec\ub9ac\ud559\uc790\uc774\uc790 \ubb38\ud559\xb7\ucca0\ud559\ubd80\ud130 \ube44\uc988\ub2c8\uc2a4 \ub300\ud654\ubc95\xb7\uc778\uac04\uad00\uacc4\uae4c\uc9c0 \uc885\ud6a1\ubb34\uc9c4 \uacbd\uacc4\ub97c \ub118\ub098\ub4e4\uba70 \uacf5\ubd80\ud558\ub294 \uba54\uc774\uc9c0\ub300\ud559\uad50\uc758 \uad34\uc9dc \uad50\uc218 \uc0ac\uc774\ud1a0 \ub2e4\uce74\uc2dc\uc758 \uc800\uc11c\uc774\ub2e4. \uc608\uc0c1\uce58 \ubabb\ud55c \uc704\uae30\uac00 \ub2e5\uce60 \ub54c\ub9c8\ub2e4 \uacf5\ubd80\ub97c \ud1b5\ud574 \uc131\uc7a5\ud574 \uc654\ub2e4\ub294 \uc800\uc790\ub294 \uc801\uc740 \uc591\uc774\ub77c\ub3c4 \uafb8\uc900\ud788 \uacf5\ubd80\ud560 \uac83\uc744 \uc8fc\ubb38\ud558\uba70, \uc9c0\ub09c 20\ub144\uac04 \uc9c1\uc811 \uacbd\ud5d8\ud558\uace0 \uc218\ubc31 \uba85\uc758 \uc81c\uc790\ub4e4\uc5d0\uac8c \uac00\ub974\uccd0 \uc900 \u2018\uc0ac\uc774\ud1a0\uc2dd \ud575\uc2ec \uacf5\ubd80\ubc95\u2019\uc744 \uc54c\ub824\uc900\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000406922"}]')}}]);