"use strict";(self.webpackChunkwrapkit=self.webpackChunkwrapkit||[]).push([[634],{742:t=>{t.exports=JSON.parse('[{"imgSrc":"../images/history/1.jpg","title":"\uc694\uc998 \uc5b4\ub978\uc744 \uc704\ud55c \ucd5c\uc18c\ud55c\uc758 \uc138\uacc4\uc0ac","author":"\uc784\uc18c\ubbf8","publicationDate":"2023.09.22","description":"\ud3bc\uce58\ub294 \uc21c\uac04 \ub2e8\uc228\uc5d0 6,000\ub144 \uc5ed\uc0ac\uac00 \uc77d\ud788\ub294","description2":"\ub7ec\uc2dc\uc544\ub294 \uc65c \uc6b0\ud06c\ub77c\uc774\ub098\uc640 \uc804\uc7c1\uc744 \ubc8c\uc774\ub294 \uac78\uae4c? \ubbf8\uad6d\uc740 \uc5b8\uc81c\ubd80\ud130 \uc804 \uc138\uacc4\ub97c \ud638\ub839\ud558\ub294 \uac15\ub300\uad6d\uc774 \ub418\uc5c8\uc744\uae4c? \u300a\uc694\uc998 \uc5b4\ub978\uc744 \uc704\ud55c \ucd5c\uc18c\ud55c\uc758 \uc138\uacc4\uc0ac\u300b\ub294 \uc624\ub298\ub0a0 \uaf2d \uc54c\uc544\uc57c \ud560 \uc138\uacc4\uc0ac\uc758 \uc8fc\uc694 \uc0ac\uac74\uc744 \ud575\uc2ec\ub9cc \uc3d9\uc3d9 \uc815\ub9ac\ud588\ub2e4. \uace0\ub300 \ubb38\uba85\uc758 \ud0c4\uc0dd\ubd80\ud130 \uac15\ub300\uad6d\uc758 \ubc1c\uc804 \uacfc\uc815\uc744 \uc2dc\uac04\uc21c\uc73c\ub85c \ube60\uc9d0\uc5c6\uc774 \uc815\ub9ac\ud574 \ud604\uc7ac\uc758 \uc138\uacc4\uc815\uc138\uac00 \uc5b4\ub5bb\uac8c \uad6c\ucd95\ub418\uc5c8\ub294\uc9c0 \uba38\ub9bf\uc18d\uc5d0 \uc138\uacc4\uc0ac\uc758 \ubf08\ub300\ub97c \ud655\uc2e4\ud558\uac8c \uc7a1\uae30 \ucda9\ubd84\ud558\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000209045024"},{"imgSrc":"../images/history/2.jpg","title":"5\ucc9c \ub144 \uc5ed\uc0ac\uac00 \ub2e8\uc228\uc5d0 \uc774\ud574\ub418\ub294 \ucd5c\uc18c\ud55c\uc758 \ud55c\uad6d\uc0ac","author":"\ucd5c\ud0dc\uc131","publicationDate":"2023.06.21","description":"\ub300\ud55c\ubbfc\uad6d \ub300\ud45c \uc5ed\uc0ac \uac15\uc0ac \ucd5c\ud0dc\uc131\uc758 5\ucc9c \ub144 \uc5ed\uc0ac\uac00 \ub2e8\uc228\uc5d0 \uc774\ud574\ub418\ub294 \ud575\uc2ec \ud55c\uad6d\uc0ac","description2":"\u2018\uc678\uc6cc\uc57c \ud560 \uac83 \ub9ce\uace0 \uc5b4\ub824\uc6b4 \ud55c\uad6d\uc0ac, \uc27d\uace0 \ube60\ub974\uace0 \uc7ac\ubbf8\uc788\uac8c \ud575\uc2ec \ub0b4\uc6a9\ub9cc \uc54c \uc218 \uc5c6\uc744\uae4c?\u2019 \ub9ce\uc740 \uc0ac\ub78c\uc774 \uc5ed\uc0ac \uacf5\ubd80\uc5d0 \uc55e\uc11c \ud488\ub294 \uc774 \uc9c8\ubb38\uc5d0 \ub2f5\ud558\uae30 \uc704\ud574 \ub300\ud55c\ubbfc\uad6d \ub300\ud45c \uc5ed\uc0ac \uac15\uc0ac \ucd5c\ud0dc\uc131\uc774 \ub098\uc130\ub2e4. \ud55c\uad6d\uc0ac \uad50\uacfc\uc11c \uc800\uc790\uc774\uc790 \ub204\uc801 \uc218\uac15\uc0dd 600\ub9cc \uba85\uc5d0 \uc774\ub974\ub294 \ud55c\uad6d\uc0ac 1\ub4f1 \uac15\uc0ac\ub85c\uc11c \ubcf5\uc7a1\ud558\uace0 \uc5b4\ub824\uc6b4 \ub9d0 \uc5c6\uc774 \uc6b0\ub9ac \uc5ed\uc0ac\uc5d0\uc11c \ubc18\ub4dc\uc2dc \uc54c\uc544\uc57c \ud560 \ud575\uc2ec \ud55c\uad6d\uc0ac\ub97c \uc5c4\uc120\ud574 \ud55c \uad8c\uc5d0 \ub2f4\uc558\ub2e4. \uace0\uc870\uc120\uc774 \uac74\uad6d\ub41c \uae30\uc6d0\uc804 2333\ub144\ubd80\ud130 6\xb715 \ub0a8\ubd81\uacf5\ub3d9\uc120\uc5b8\uc774 \ubc1c\ud45c\ub41c 2000\ub144\uae4c\uc9c0, \ubc18\ub9cc\ub144 \uc5ed\uc0ac\uac00 \ub2e8\uc228\uc5d0 \uc774\ud574\ub418\ub294 \ud55c\uad6d\uc0ac \uc785\ubb38\uc11c \u300a\ucd5c\uc18c\ud55c\uc758 \ud55c\uad6d\uc0ac\u300b\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000202678286"},{"imgSrc":"../images/history/3.jpg","title":"\uac70\uafb8\ub85c \uc77d\ub294 \uc138\uacc4\uc0ac","author":"\uc720\uc2dc\ubbfc","publicationDate":"2021.10.29","description":"\ud55c \uc2dc\ub300\ub97c \ud48d\ubbf8\ud588\ub358 \ubca0\uc2a4\ud2b8\uc140\ub7ec\uc758 \uadc0\ud658, 100\ub9cc \ub3c5\uc790\ub97c \uc0ac\ub85c\uc7a1\uc740 \u2018\uc774\uc57c\uae30\uc758 \ud798\u2019","description2":"\u300e\uac70\uafb8\ub85c \uc77d\ub294 \uc138\uacc4\uc0ac\u300f\ub294 \uc720\uc2dc\ubbfc\uc5d0\uac8c \uc5ec\ub7ec \ubaa8\ub85c \u2018\uccab \ubc88\uc9f8\u2019\ub85c\uc11c \uac16\ub294 \uc758\ubbf8\uac00 \ub9ce\ub2e4. \ucc98\uc74c\uc73c\ub85c \u2018\uc791\uac00\u2019\ub77c\ub294 \uc774\ub984\uc744 \ub2ec\uc544\uc900 \ucc45\uc774\uc790, \uc800\uc11c \uc911 \uac00\uc7a5 \uba3c\uc800 \ub2e8\uc2dc\uac04\uc5d0 \ubca0\uc2a4\ud2b8\uc140\ub7ec\uc5d0 \uc624\ub978 \ucc45\uc778 \ub3d9\uc2dc\uc5d0 \uac00\uc7a5 \uc624\ub7ab\ub3d9\uc548 \ub3c5\uc790 \uacc1\uc5d0 \uba38\ubb38 \ucc45\uc774\ub2e4. \uc9c0\uc2dd\uc18c\ub9e4\uc0c1 \uc720\uc2dc\ubbfc\uc744 \ubcf8\uaca9\uc801\uc73c\ub85c \uc54c\ub9b0, \u300e\ub098\uc758 \ud55c\uad6d\ud604\ub300\uc0ac 1959-2020\u300f(\ub3cc\ubca0\uac1c 2021), \u300e\uc5ed\uc0ac\uc758 \uc5ed\uc0ac\u300f(\ub3cc\ubca0\uac1c, 2018)\ub97c \uc788\uac8c \ud55c \u2018\uc720\uc2dc\ubbfc\uc758 \uc5ed\uc0ac 3\ubd80\uc791\u2019\uc758 \uc2dc\uc791\uc810\uc774\uae30\ub3c4 \ud558\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001951342"},{"imgSrc":"../images/history/4.jpg","title":"\uc138\uc0c1\uc5d0\uc11c \uac00\uc7a5 \uc9e7\uc740 \ub3c5\uc77c\uc0ac","author":"\uc81c\uc784\uc2a4 \ud638\uc988","publicationDate":"2023.11.22","description":"\ub3c5\uc77c \uc5ed\uc0ac\uc758 \ud558\uc774\ub77c\uc774\ud2b8\ub97c \uc5ed\uc0ac\uac00\uc758 \ub0a8\ub2e4\ub978 \ud654\ub824\ud55c \ud544\uccb4\ub85c \uadf8\ub824\ub0b8 \uc5ed\uc791!","description2":"\u300e\uc138\uc0c1\uc5d0\uc11c \uac00\uc7a5 \uc9e7\uc740 \ub3c5\uc77c\uc0ac\u300f\ub294 \uc57c\ub9cc\uacfc \uc774\uc131, \ubbfc\uc8fc\uc8fc\uc758\uc640 \uad70\uad6d\uc8fc\uc758, \uacf5\uc874\uacfc \ubc30\uc81c, \uc808\uc81c\uc640 \ud0d0\uc695\uae4c\uc9c0, \uc0c1\ubc18\ub41c \uac1c\ub150\ub4e4\uc774 \ub4a4\uc11e\uc778 \ub3c5\uc77c\uc0ac\uc758 \ubcf8\uc9c8\uc744 \ub0c9\ucca0\ud558\uac8c \ud30c\ud5e4\uce58\uace0 \uc788\ub2e4. \uace0\ub300 \uc720\ub7fd\uc744 \uc9c0\ubc30\ud588\ub358 \ub85c\ub9c8\uc81c\uad6d\uc744 \ud30c\uad34\ud558\ub294 \ub370 \uc77c\uc870\ud558\uba74\uc11c, \ud55c\ud3b8\uc73c\ub85c \uadf8\ub4e4\uc774 \ube5b\ub098\ub294 \uadf8\ub9ac\uc2a4, \ub85c\ub9c8\uc758 \uc9c0\uc801 \uc720\uc0b0\uc758 \ubcf5\uc6d0\uc5d0 \uc5b4\ub5bb\uac8c \uae30\uc5ec\ud588\ub294\uc9c0 \uc9d1\uc5b4\uc900\ub2e4. \ub098\uc544\uac00 \uc885\uad50\uac1c\ud601, \ud504\ub791\uc2a4\uc640\uc758 \ub300\uacb0, \uc138\uacc4\ub300\uc804, \ubd84\ub2e8\uacfc \ud1b5\uc77c\uae4c\uc9c0 \ub9ce\uc740 \uc5ed\uc0ac\uc801 \uc8fc\uc694 \uc774\uc815\ud45c\ub97c \uba74\ubc00\ud558\uac8c \uac80\uc99d\ud558\uace0 \uac00\ucc28 \uc5c6\uc774 \uc5ed\uc0ac\uac00\ub85c\uc11c\uc758 \uba54\uc2a4\ub97c \uac00\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000211235634"},{"imgSrc":"../images/history/5.jpg","title":"\ubc8c\uac70\ubc97\uc740 \uc138\uacc4\uc0ac: \uc804\uc7c1\ud3b8","author":"tvn<\ubc8c\uac70\ubc97\uc740\uc138\uacc4\uc0ac>\uc81c\uc791\ud300","publicationDate":"2022.07.27","description":"\ubc97\uacbc\ub2e4, \ub05d\ub098\uc9c0 \uc54a\ub294 \uc804\uc7c1","description2":"\uc774 \ucc45\uc740 tvN\uc5d0\uc11c \ubc29\uc601\ud55c \uc2a4\ud1a0\ub9ac\ud154\ub9c1 \uc138\uacc4\uc0ac \u3008\ubc8c\uac70\ubc97\uc740 \uc138\uacc4\uc0ac\u3009\uc5d0\uc11c \ub2e4\ub918\ub358 \ub0b4\uc6a9 \uc911 \uc804\uc7c1\uc5d0 \uad00\ud55c \uc0ac\uac74\ub4e4\uc744 \ubaa8\uc544 \ub9cc\ub4e0 \uac83\uc785\ub2c8\ub2e4. \uc138\uacc4 \uc5ed\uc0ac\uc5d0 \ud070 \ud68d\uc744 \uadf8\uc740 \uc21c\uac04\uc740 \ubb3c\ub860, \ucc98\uc74c \ub9cc\ub098\ub294 \uc758\uc678\uc758 \uc0ac\uc2e4\ub4e4\uae4c\uc9c0 \ub354\ud574 \uadf8\ub3d9\uc548 \uc6b0\ub9ac\uac00 \ubcf4\uc9c0 \ubabb\ud588\ub358 \ud504\ub808\uc784 \ubc16\uc758 \uc804\uc7c1\uc0ac\ub97c \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. \ud504\ub791\uc2a4\uc640 \uc601\uad6d\uc774 \ubb34\ub824 116\ub144\uac04 \ubc8c\uc778 \ubc31\ub144\uc804\uc7c1\ubd80\ud130 \ud604\uc7ac \uc9c4\ud589\ud615\uc778 \uc6b0\ud06c\ub77c\uc774\ub098 \uc804\uc7c1\uae4c\uc9c0, \uc138\uacc4\ub97c \ub4a4\ud754\ub4e0 \uc804\uc7c1\uc758 \uc5ed\uc0ac\uac00 \uc785\uccb4\uc801\uc73c\ub85c \ud3bc\uccd0\uc9d1\ub2c8\ub2e4. ","link":"https://product.kyobobook.co.kr/detail/S000061532612"},{"imgSrc":"../images/history/6.jpg","title":"\uc774\uc21c\uc2e0\uc758 \ubc14\ub2e4","author":"\ud669\ud604\ud544","publicationDate":"2021.12.16","description":"\uadf8 \ubc14\ub2e4\ub294 \ubb34\uc5c7\uc744 \uc0bc\ucf30\ub098","description2":"\uace0\ub824\ub9d0 \uc65c\uad6c\ubd80\ud130 \ub3d9\ubd81\uc544 \ubc14\ub2e4\ub97c \uc8fc\ub984\uc7a1\ub358 \ucd5c\uac15 \uc77c\ubcf8\uc758 \uc218\uad70\uc740 \uc65c \uadf8\ub807\uac8c \ubb34\uae30\ub825\ud588\uc744\uae4c? \uadf8\ub4e4\uc740 \uc65c \uc790\uad6d\uc758 \uc5f0\ud574 \ubc16\uc73c\ub85c \ubc30 \ud55c \ucc99 \ub04c\uace0 \ub098\uc624\uc9c0 \ubabb\ud588\ub358 \ud3d0\uc1c4\uc801\uc778 \uc870\uc120 \uc218\uad70\uc758 \uc0ac\ub839\uad00 \uc774\uc21c\uc2e0\uc5d0\uac8c \ucd5c\uc545\uc758 \uc5f0\ud328\ub97c \ub2f9\ud588\uc744\uae4c? \uadf8 \ubbf8\uc2a4\ud14c\ub9ac\ud568\uc744 \uc2dc\uc6d0\ud558\uace0 \ud1b5\ucf8c\ud558\uba70, \uac10\ub3d9\uc801\uc73c\ub85c, \uadf8\ub9ac\uace0 \uc2dc\uac01\uc801\uc73c\ub85c \ud480\uc5b4\ub0b4\uc5c8\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001987936"}]')}}]);