"use strict";(self.webpackChunkwrapkit=self.webpackChunkwrapkit||[]).push([[812],{812:i=>{i.exports=JSON.parse('[{"imgSrc":"../images/science/1.jpg","title":"\uc774\uae30\uc801 \uc720\uc804\uc790","author":"\ub9ac\ucc98\ub4dc \ub3c4\ud0a8\uc2a4","publicationDate":"2023.01.30","description":"\ub3c5\ud2b9\ud55c \ubc1c\uc0c1\uacfc \ub180\ub77c\uc6b4 \uc8fc\uc7a5\uc73c\ub85c 40\uc5ec \ub144\uac04 \uc218\ub9ce\uc740 \ucc2c\uc0ac\uc640 \ub17c\uc7c1\uc758 \uc911\uc2ec\uc5d0 \uc120 \uacfc\ud559 \uad50\uc591\uc11c\uc758 \ubc14\uc774\ube14!","description2":"1976\ub144, \ucc98\uc74c \ucd9c\uac04\ub418\uc5c8\uc744 \ub2f9\uc2dc \uacfc\ud559\uacc4\uc640 \uc77c\ubc18 \ub300\uc911\ub4e4\uc5d0\uac8c \ud3ed\ubc1c\uc801\uc778 \ubc18\ud5a5\uc744 \ubd88\ub7ec\uc77c\uc73c\ud0a4\uba70 \uc138\uae30\uc758 \ubb38\uc81c\uc791\uc73c\ub85c \ub5a0\uc624\ub978 \u300e\uc774\uae30\uc801 \uc720\uc804\uc790\u300f\ub294 40\ub144\uc774\ub77c\ub294 \uc138\uc6d4\uc758 \uac80\uc99d\uc744 \uac70\uce58\uba70 \uadf8 \uc911\uc694\uc131\uacfc \uae4a\uc774\ub97c \ub354\uc6b1\ub354 \ud655\uace0\ud558\uac8c \uc778\uc815\ubc1b\uc558\uace0, 25\uac1c \uc774\uc0c1\uc758 \uc5b8\uc5b4\ub85c \ubc88\uc5ed\ub418\uc5c8\uc73c\uba70 \uc80a\uc740\uc774\ub4e4\uc774 \uaf2d \uc77d\uc5b4\uc57c \ud560 \uacfc\ud559\uacc4\uc758 \uace0\uc804\uc73c\ub85c \uc790\ub9ac \uc7a1\uc558\ub2e4. \uc0c8\ub85c\uc6b4 \ub514\uc790\uc778\uacfc \ud734\ub300\ud558\uae30 \uc88b\uc740 \ud310\ud615\uc73c\ub85c \uac08\uc544 \uc785\uc740 \uc774\ubc88 40\uc8fc\ub144 \uae30\ub150\ud310\uc5d0 \uc0c8\ub86d\uac8c \uc218\ub85d\ub41c \uc5d0\ud544\ub85c\uadf8\uc5d0\uc11c \uc800\uc790\ub294 \uc5ec\uc804\ud788 \u2018\uc774\uae30\uc801 \uc720\uc804\uc790\u2019\ub77c\ub294 \uac1c\ub150\uc774 \uac16\uace0 \uc788\ub294 \uc9c0\uc18d\uc801\uc778 \ud0c0\ub2f9\uc131\uc744 \uc774\uc57c\uae30\ud558\uba70 \uc774 \ucc45\uc774 \uc804\ud558\ub294 \uba54\uc2dc\uc9c0\ub97c \ub418\uc0c8\uae34\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000576524"},{"imgSrc":"../images/science/2.jpg","title":"\ucf54\uc2a4\ubaa8\uc2a4","author":"\uce7c \uc138\uc774\uac74","publicationDate":"2006.12.20","description":"\uacfc\ud559 \uad50\uc591\uc11c\uc758 \uace0\uc804","description2":"\uacfc\ud559 \uad50\uc591\uc11c\uc758 \uace0\uc804\u300e\ucf54\uc2a4\ubaa8\uc2a4\u300f. \uc774 \ucc45\uc5d0\uc11c \uc800\uc790\ub294 \uc6b0\uc8fc\uc758 \ud0c4\uc0dd\uacfc \uc740\ud558\uacc4\uc758 \uc9c4\ud654, \ud0dc\uc591\uc758 \uc0b6\uacfc \uc8fd\uc74c, \uc6b0\uc8fc\ub97c \ub5a0\ub3cc\ub358 \uba3c\uc9c0\uac00 \uc758\uc2dd \uc788\ub294 \uc0dd\uba85\uc774 \ub418\ub294 \uacfc\uc815, \uc678\uacc4 \uc0dd\uba85\uc758 \uc874\uc7ac \ubb38\uc81c \ub4f1\uc5d0 \uad00\ud55c \ub0b4\uc6a9\uc744 \uc218 \ubc31\uc7a5\uc758 \uc0ac\uc9c4\uacfc \uc77c\ub7ec\uc2a4\ud2b8\ub97c \uacc1\ub4e4\uc5ec \ud765\ubbf8\ub86d\uac8c \uc124\uba85\ud55c\ub2e4. \ud604\ub300 \ucc9c\ubb38\ud559\uc744 \ub300\ud45c\ud558\ub294 \uc800\uba85\ud55c \uacfc\ud559\uc790\uc778 \uc800\uc790\ub294 \uc774 \ucc45\uc5d0\uc11c \uc0ac\ub78c\ub4e4\uc758 \uc0c1\uc0c1\ub825\uc744 \uc0ac\ub85c\uc7a1\uace0, \ub09c\ud574\ud55c \uac1c\ub150\uc744 \uba85\ucf8c\ud558\uac8c \ud574\uc124\ud558\ub294 \ub180\ub77c\uc6b4 \ub2a5\ub825\uc744 \ub9c8\uc74c\uaecf \ubc1c\ud718\ud55c\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001290624"},{"imgSrc":"../images/science/3.jpg","title":"\uc885\uc758 \uae30\uc6d0","author":"\ucc30\uc2a4 \ub2e4\uc708","publicationDate":"2019.07.31","description":"\ud55c\uad6d \uc9c4\ud654 \uc0dd\ubb3c\ud559\uacc4\uc758 \uc5ed\ub7c9\uc744 \uacb0\uc9d1\ud55c \ub2e4\uc708 \uc120\uc9d1!","description2":"21\uc138\uae30 \ud604\uc7ac\ub3c4 \uc790\uc5f0 \uacfc\ud559\uc740 \ubb3c\ub860, \uc778\ubb38 \uc0ac\ud68c \uacfc\ud559 \ub4f1 \ud559\ubb38 \uc138\uacc4 \uc804\ubc18\uc5d0\uc11c \uac15\ub825\ud55c \uc601\ud5a5\ub825\uc744 \ubc1c\ud718\ud558\uace0 \uc788\ub294 19\uc138\uae30 \uc601\uad6d\uc758 \uc0dd\ubb3c\ud559\uc790\uc774\uc790 \uc9c0\uc9c8\ud559\uc790\uc774\uba70 \ubc15\ubb3c\ud559\uc790\uc778 \ucc30\uc2a4 \ub2e4\uc708. \ucd5c\uc7ac\ucc9c \uc774\ud654\uc5ec\ub300 \uc5d0\ucf54 \uacfc\ud559\ubd80 \uad50\uc218\uac00 \uc774\ub048 \ub2e4\uc708 \ud3ec\ub7fc\uc774 \uae30\ud68d\ud558\uace0 \uac10\uc218\ud55c \ud55c\uad6d \uc9c4\ud654 \uc0dd\ubb3c\ud559\uacc4\uc758 \uc5ed\ub7c9\uc744 \uacb0\uc9d1\ud55c \ub2e4\uc708 \uc120\uc9d1 \u300c\ub4dc\ub514\uc5b4 \ub2e4\uc708\u300d \uc2dc\ub9ac\uc988\ub97c \ud1b5\ud574 \ub2e4\uc708\uc758 \uc8fc\uc694 \uc800\uc791\uc758 \ubc88\uc5ed \uc815\ubcf8\uc744 \ub9cc\ub098\ubcfc \uc218 \uc788\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001923922"},{"imgSrc":"../images/science/4.jpg","title":"\uc544\uba54\ub9ac\uce78 \ud504\ub85c\uba54\ud14c\uc6b0\uc2a4","author":"\uce74\uc774 \ubc84\ub4dc","publicationDate":"2023.06.12","description":"\uc6d0\uc790 \ud3ed\ud0c4\uc758 \uc544\ubc84\uc9c0, \ub85c\ubc84\ud2b8 \uc624\ud39c\ud558\uc774\uba38\uc758 \uc601\uad11\uacfc \ubab0\ub77d\uc758 \uc11c\uc0ac\uc2dc!","description2":"\uc81c2\ucc28 \uc138\uacc4 \ub300\uc804 \uc2b9\ub9ac\ub97c \ud5a5\ud55c \uacbd\uc7c1 \uc18d\uc5d0\uc11c \ud0dc\uc5b4\ub09c \ud575\ubb34\uae30\ub294 \uc774\ubbf8 \ud0c4\uc0dd \uc9c1\ud6c4 \uc5c4\uccad\ub09c \ud30c\uad34\ub825\uacfc \ub0a8\uc6a9 \uac00\ub2a5\uc131\uc73c\ub85c \uc778\ud574 \ub9d0 \uadf8\ub300\ub85c \ud3ed\ud0c4\uc774 \ub418\uc5b4 \uc654\ub2e4. \uadf8\ub9ac\uace0 \uc6d0\uc790 \ud3ed\ud0c4\uc744 \uac1c\ubc1c\ud558\ub294 \ub9e8\ud574\ud2bc \ud504\ub85c\uc81d\ud2b8\uc758 \ucd1d\uc9c0\ud718\uc790\uc774\uc790 \uc6d0\uc790 \ud3ed\ud0c4\uc758 \uc544\ubc84\uc9c0 \uc624\ud39c\ud558\uc774\uba38\uc758 \uc77c\uc0dd\uc5d0 \uc788\uc5b4\uc11c\ub3c4 \uadf9\uc801\uc778 \uc21c\uac04\ub4e4\uc744 \uc548\uaca8 \uc8fc\uc5c8\ub2e4. \uc800\ub110\ub9ac\uc2a4\ud2b8\uc778 \uce74\uc774 \ubc84\ub4dc\uc640 \uc601\ubb38\ud559\uacfc \ubbf8\uad6d \uc5ed\uc0ac\ud559 \uad50\uc218\uc778 \ub9c8\ud2f4 \uc154\uc708 \ub450 \uc0ac\ub78c\uc758 \uc800\uc790\uac00 25\ub144 \ub3d9\uc548 \ub2f5\uc0ac\uc640 \uc778\ud130\ubdf0, FBI \ubb38\uc11c \uc5f4\ub78c \ub4f1 \uc790\ub8cc \uc218\uc9d1\uc744 \uac70\uccd0 \uc4f4 \uc774 \ucc45\uc740 2005\ub144 \ucc98\uc74c \ucd9c\uac04\ub418\uc790\ub9c8\uc790 \uc804\ubbf8 \ub3c4\uc11c \ube44\ud3c9\uac00 \ud611\ud68c \uc804\uae30 \ubd80\ubb38(The National Book Critics Circle(NBCC) Award for Biography)\uc744 \uc218\uc0c1\ud558\uace0 2006\ub144\uc5d0\ub294 \ud4f0\ub9ac\ucc98 \uc0c1 \uc804\uae30\xb7\uc790\uc11c\uc804 \ubd80\ubb38(Pulitzer Prize for Biography or Autobiography)\uc744 \uc218\uc0c1\ud55c \ubc14 \uc788\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000202687816"},{"imgSrc":"../images/science/5.jpg","title":"\uc5d4\ud2b8\ub85c\ud53c","author":"\uc81c\ub808\ubbf8 \ub9ac\ud504\ud0a8","publicationDate":"2015.04.01","description":"\uc5d4\ud2b8\ub85c\ud53c \ubc95\uce59\uc774\ub780 \ubb34\uc5c7\uc778\uac00?","description2":"\u2018\ubb3c\uc9c8\uc774 \uc5f4\uc5ed\ud559\uc801 \ubcc0\ud654\ub97c \uc77c\uc73c\ud0ac \ub54c \ubcc0\ud654\ub41c \uc628\ub3c4\ub97c \uc5f4\ub7c9\uc73c\ub85c \ub098\ub208 \uac12\uc73c\ub85c\uc11c, \uc4f8 \uc218 \uc5c6\uac8c \ub41c \uc5d0\ub108\uc9c0\u2019\ub97c \ub73b\ud558\ub294 \uc5d4\ud2b8\ub85c\ud53c\ub294 \uac00\uc6a9 \uc5d0\ub108\uc9c0\ub97c \ucd08\uacfc\ud558\ub294 \uc0c1\ud669\uc5d0 \ub300\ud55c \uacbd\uace0\ub97c \ub2f4\uace0 \uc788\ub294 \ucc45\uc774\ub2e4. \uc774\ub97c \ud1b5\ud574\uc11c \uc5ed\uc0ac\ub97c \uc9c4\ubcf4\ub85c \ubcf4\ub294 \uc2dc\uac01\uc744 \ubb34\ub108\ub728\ub9ac\uace0, \uacfc\ud559\uacfc \uae30\uc220\uc774 \ubcf4\ub2e4 \uc9c8\uc11c \uc788\ub294 \uc0ac\ud68c\ub97c \ub9cc\ub4e4 \uac83\uc774\ub77c\ub294 \ud658\uc0c1\uc744 \ub2e4\uc2dc \ud55c \ubc88 \uc0dd\uac01\ud574\ubcf4\uac8c \ud574\uc900\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000001366514"},{"imgSrc":"../images/science/6.jpg","title":"\ub354 \uc704\ud5d8\ud55c \uacfc\ud559\ucc45","author":"\ub79c\ub4e4 \uba3c\ub85c","publicationDate":"2020.01.25","description":"\uc9c0\uad6c\uc778\uc774\ub77c\uba74 \ubc18\ub4dc\uc2dc \ubd10\uc57c \ud560 \ud5c8\ub97c \ucc0c\ub974\ub294 \uc77c\uc0c1 \uc18d \uacfc\ud559 \uc6d0\ub9ac\ub4e4","description2":"\uadf8\uc758 \ucc45\uc5d4 \ub2e4\ub978 \uadf8 \uc5b4\ub514\uc11c\ub3c4 \ucc3e\uc544\ubcfc \uc218 \uc5c6\ub294 \uae30\ubc1c\ud55c \uc0c1\uc0c1\ub825\uacfc \uadf8 \uc0c1\uc0c1\ub825\uc744 \uacfc\ud559\uc801\uc778 \ubc29\ubc95\uc73c\ub85c \ud480\uc5b4\uac00\ub294 \ub180\ub77c\uc6c0\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc9d1\uc744 \ud1b5\uc9f8\ub85c \ub0a0\ub824\uc11c \uc774\uc0ac\ud558\ub294 \ubc29\ubc95\uc774\ub098 \ub098\ube44\uc758 \ub0a0\uac1c\uc5d0 \ud30c\uc77c\uc744 \uc2e4\uc5b4 \ud574\uc678\uc5d0 \uc804\uc1a1\ud558\ub294 \ubc95\uc744 \uacfc\ud559\uc801\uc73c\ub85c \uc54c\ub824\uc900\ub2e4\ub358\uc9c0, \uc6b0\uc0ac\uc778 \ubcfc\ud2b8\uc640 \uc220\ub798\uc7a1\uae30\ub97c \ud55c\ub2e4\uac70\ub098 \uc6b0\uc8fc\uc5d0\uc11c \uc18c\ud3ec\ub97c \ubd80\uce58\ub294 \ubc29\ubc95\uc744 \uc54c\ub824\uc8fc\ub294 \ub4f1, \uae30\uc0c1\ucc9c\uc678\ud55c \ubc1c\uc0c1\uc5d0 \uc6c3\uc74c\uc744 \uc9d3\ub2e4\uac00 \uc5b4\ub290\uc0c8 \uc790\uc5f0\uc2a4\ub7fd\uac8c \uacfc\ud559\uc801 \uc9c0\uc2dd\uacfc \uc815\ubcf4\ub97c \uc2b5\ub4dd\ud558\uac8c \ub418\ub294 \ub180\ub77c\uc6b4 \uc77c\uc774 \uc77c\uc5b4\ub098\uac8c \ub429\ub2c8\ub2e4. \ub79c\ub4e4 \uba3c\ub85c\uc758 \ucc45\uc744 \uc77d\uac8c \ub41c\ub2e4\uba74, \ub2f9\uc2e0\ub3c4 \uc774 \ub180\ub77c\uc6b4 \uc77c\uc744 \uacaa\uac8c \ub420 \uac83\uc785\ub2c8\ub2e4.","link":"https://product.kyobobook.co.kr/detail/S000000731728"}]')}}]);